/* 투자 프로젝트 등록 */
SELECT (CASE WHEN COUNT(i.investment_code) = 0 THEN CONCAT('inv_', CURDATE()+0, '_0001') WHEN COUNT(i.investment_code) > 9999 THEN CONCAT('inv_', CURDATE()+0, '_', MAX(CAST(SUBSTRING_INDEX(i.investment_code,'_',-1) AS UNSIGNED))+1) ELSE CONCAT('inv_', CURDATE()+0, '_', LPAD(MAX(CAST(SUBSTRING_INDEX(i.investment_code,'_',-1) AS UNSIGNED))+1, 4, '0')) END) AS investmentCode FROM investment AS i WHERE SUBSTRING_INDEX(SUBSTRING_INDEX(i.investment_code, '_', 2), '_', -1) = CURDATE()+0;
INSERT INTO investment (investment_code, member_id, store_code, investment_request_judge_code, total_category_code, member_id_seller, investment_subject, investment_image, investment_like, investment_amount, investment_achievement_money, invest_achievement_percent, investment_company, search_count, invest_count, securities_classification, investment_display_date, investment_reg_date, investment_deadline, investment_report, basic_fee, contract_fee_rate, contract_file, invest_judge_situation) values (#{investmentCode},'id001', 'sto004', #{investmentRequestJudgeCode}, #{totalCategoryCode}, 'id002', #{investmentSubject}, #{investmentImage}, '0', '0', '0', '0', #{investmentCompany}, '0', '0', #{securitiesClassification}, CURDATE(), #{investmentRegDate}, #{investmentDeadline}, #{investmentReport}, '3000000', 'null', #{contractFile}, '0');

/* 투자 프로젝트 상세내용 등록 */
SELECT (CASE WHEN COUNT(i.investment_content_code) = 0 THEN CONCAT('inv_con_', CURDATE()+0, '_0001') WHEN COUNT(i.investment_content_code) > 9999 THEN CONCAT('inv_con_', CURDATE()+0, '_', MAX(CAST(SUBSTRING_INDEX(i.investment_content_code,'_',-1) AS UNSIGNED))+1) ELSE CONCAT('inv_con_', CURDATE()+0, '_', LPAD(MAX(CAST(SUBSTRING_INDEX(i.investment_content_code,'_',-1) AS UNSIGNED))+1, 4, '0')) END) AS investmentContentCode FROM investment_content AS i WHERE SUBSTRING_INDEX(SUBSTRING_INDEX(i.investment_content_code, '_', 3), '_', -1) = CURDATE()+0;
